/**
 * @fileOverview lazyLoad jQuery plugin
 * @author       Valerio "Lotti" Riva
 * @version      1.0.0
 */

/**
 * jQuery object
 * @external "jQuery"
 * @see {@link http://api.jquery.com/jQuery/}
 */

/**
 * The jQuery plugin namespace.
 * @external "jQuery.fn"
 * @see {@link http://learn.jquery.com/plugins/|jQuery Plugins}
 */

(function($){
    $.fn.extend({
        /**
         * lazyLoad - a plugin that implements image lazy loading.
         * Created by lotti on 26/04/15.
         *
         * @name lazyLoad
         * @class
         */
        lazyLoad: function(options) {
            var defaults = {
                selector: '.lazy',
                placeholder: 'data:image/gif;base64,'
            };

            //merging default options with custom ones.
            options = $.extend(defaults, options);

            /**
             * This array will contain every image that needs to be loaded on window scrolling.
             *
             * @type {Array}
             */
            var toBeLoaded = [];

            /**
             * Tests is an element is below the fold
             *
             * @name lazyLoad#isBelowTheFold
             * @function
             * @param {jQuery} $img - the image to test.
             * @returns {Boolean}
             */
            var isBelowTheFold = function($element) {
                var fold = (window.innerHeight ? window.innerHeight : $(window).height()) + $(window).scrollTop();
                return fold <= $element.offset().top;
            };

            /**
             * Checks if the specified image is above or below the fold.
             *
             * @name lazyLoad#checkImage
             * @function
             * @param {jQuery} $img - the image to check.
             */
            var checkImage = function($img) {
                if (isBelowTheFold($img)) {
                    toBeLoaded.push($img);
                    $img.attr('src', options.placeholder);
                }
                else {
                    restoreImage($img);
                }
            };

            /**
             * This method restores the src attribute to a specific image,
             * and it marks the image as loaded (by removing the image from toBeLoaded array).
             *
             * @name lazyLoad#restoreImage
             * @function
             * @param {jQuery} $img - the image that has to be restored.
             */
            var restoreImage = function($img) {
                $img.attr('src', $img.attr('data-src'));
                $img.removeAttr('data-src');
                var index = toBeLoaded.indexOf($img);
                if (index > -1) {
                    toBeLoaded.splice(index, 1);
                }
            };

            /**
             * Variable used as a lock, for synchronization purpose.
             *
             * @type {boolean}
             */
            var managingQueue = false;

            /**
             * Manages the images not loaded queue.
             * This function is structured to run only if is not already running,
             * so it safes to have it called 100k times from the window onscroll event.
             *
             * It cycles the images not loaded and, when it finds one that has gone above the fold,
             * it loads images one by one.
             *
             * @name lazyLoad#manageQueue
             * @function
             */
            var manageQueue = function() {
                if (!managingQueue && toBeLoaded.length > 0) {
                    managingQueue = true;
                    for(var i in toBeLoaded) {
                        var $img = toBeLoaded[i];
                        if (!isBelowTheFold($img)) {
                            //using plain javascript to load the image from data-src attribute
                            var image = new Image();
                            image.src = $img.attr('data-src');
                            image.onload = function () {
                                restoreImage($img);
                                managingQueue = false;
                                manageQueue();
                            };
                            break;
                        }
                    }
                    managingQueue = false;
                }
            };

            /**
             * Binds a call to manageQueue method each time onscroll event is fired by window.
             */
            $(window).scroll(function() {
                manageQueue();
            });

            return this.each(function() {
                var obj = $(this);
                //searching all images that matches the selector and are children of a specific parent
                $('img'+ options.selector,obj).each(function(i) {
                    //every image must be checked if it is above or below the fold
                    checkImage($(this));
                });
            });
        }
    });
})(jQuery);

